<?phpfunction mmwcptwd_plugin_url( $path = '' ) {  $url = plugins_url( $path, MMWCPTWD_PLUGIN );  if ( is_ssl()  and 'http:' == substr( $url, 0, 5 ) ) {    $url = 'https:' . substr( $url, 5 );  }  return $url;}function mmwcptwd_submit_button( $text = 'Save Changes', $type = 'btn btn-info save-and-add-cptwd save_button save_mail', $name = 'submit', $wrap = true, $other_attributes = null ) {	echo get_submit_button( $text, $type, $name, $wrap, $other_attributes );}